<script>
	import { goto } from '$app/navigation';
	import { page } from '$app/stores';
	import routes from '$lib/routes';
	import { stringToColor } from '$lib/utils';
	import { toast } from '@zerodevx/svelte-toast';
	import { fade } from 'svelte/transition';
	import { fadeConfig } from '$lib/config';
	import { hasToSearch } from '$lib/stores/search-store';

	const user = $page.data.dbUser;
	const isOwner = user ? $page.data.prompt.author.email === user.email : false;
	let likes = $page.data.prompt.likes.length;
	let showCopyFeedback = false;
	let hartIcon = getHartIcon();
	$: forkLink = routes.fork(user, $page.params.slug);
	$: appreciationText = likes;

	function getHartIcon() {
		if (user) {
			const liked = $page.data.prompt.likes.find((like) => like.userId === user.id);
			if (liked) {
				return 'fullhart';
			}
		}
		return 'hart';
	}

	async function handleAddRemoveLike() {
		if (!user) {
			goto('/login');
			return;
		}

		if (hartIcon == 'hart') {
			hartIcon = 'fullhart';
			likes++;
		} else {
			hartIcon = 'hart';
			likes--;
		}

		try {
			const r = await fetch(`/api/add-remove-like?id=${$page.data.prompt.id}`, {
				method: 'POST'
			});

			const json = await r.json();
			if (json.status != 200) {
				toast.push('Something went wrong. Please try again.');
			}
		} catch (e) {
			toast.push('Something went wrong. Please try again.');
			console.log(e);
		}
	}

	function copyToClipboard() {
		navigator.clipboard.writeText($page.data.prompt.content);
		showCopyFeedback = true;

		setTimeout(() => {
			showCopyFeedback = false;
		}, 2000);
	}
	function handleTagClick() {
		hasToSearch.set(true);
	}
</script>

<svelte:head>
	<title>{$page.data.prompt.name} | We Prompt</title>
	<meta name="description" content={$page.data.prompt.description} />
</svelte:head>

<main in:fade={fadeConfig}>
	<div class="header">
		<div class="top-bar">
			<a class="author" href={routes.profile($page.data.prompt.author.username)}>
				<img src={$page.data.prompt.author.image} alt="" />
				<p>
					<span>created by</span>
					<br />
					{$page.data.prompt.author.username}
				</p>
			</a>

			<div class="actions">
				<button class="bubble" on:click={handleAddRemoveLike}>
					{#if hartIcon == 'fullhart'}
						<img
							class="icon"
							alt="fullhart"
							src="data:image/png;base64,"
						/>
					{:else}
						<img
							class="icon"
							alt="hart"
							src="data:image/png;base64,"
						/>
					{/if}
					{appreciationText}
				</button>

				<a href={forkLink}>
					<span class="forked bubble">
						<img class="icon" src="/fork-icon.png" alt="fork-icon" />
						{$page.data.prompt.forkedCount}
					</span>
				</a>
			</div>
		</div>

		<div class="name">
			<h1>{$page.data.prompt.name}</h1>
			{#if isOwner}
				<a class="edit-link" href={routes.edit($page.params.slug)}>Edit</a>
			{/if}
		</div>

		<div class="infos">
			<a class="model" target="_blank" href={$page.data.prompt.aiModel.link}>
				<span>
					{$page.data.prompt.aiModel.name}
				</span>
				<img src="/link.svg" alt="link" />
			</a>

			<button class="copy-btn bubble" on:click={copyToClipboard}>
				{#if showCopyFeedback}
					<img class="tick-icon" src="/tick.png" alt="tick-icon" />
					Copied!
				{:else}
					<svg class="copy-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
						<path
							d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2"
						/>
						<rect x="8" y="2" width="8" height="4" rx="1" ry="1" />
					</svg>
					Copy prompt
				{/if}
			</button>
		</div>

		<div class="tags">
			{#each $page.data.prompt.tags as tag (tag.id)}
				<a
					class="bubble"
					style={`background-color: ${stringToColor(tag.name)}`}
					href={`/search?tag=${tag.name}&limit=10&page=0`}
					on:click={handleTagClick}
				>
					#{tag.name}
				</a>
			{/each}
		</div>
	</div>

	<div class="body">
		<h2>Description</h2>
		<p class="description">{$page.data.prompt.description}</p>

		<h2>Prompt</h2>
		<p class="prompt">{$page.data.prompt.content}</p>
	</div>
</main>

